  hexToByte: function(str) {
    //TODO:
    /*
    if (!str) {
      return new Uint8Array();
    }
*/
    var a = [];
    for (var i = 0, len = str.length; i < len; i+=2) {
      a.push(parseInt(str.substr(i,2),16));
    }

    return new Uint8Array(a);
